/*

Convention
===========
.p-value is for property: value

example: .d-block is for display: block

-----------------------------------------

for properties with more than one word, take the first letter of each word followed by -value
for example: .br-50 is for border-radius: 50%

Units
=====
Numbers without units are meant to be percentages, for example .w-100 means width: 100% unless there are units, for example .w-100vw means width: 100vw
*/

/* globals */
body {
  box-sizing: border-box;
  padding: 0;
  margin: 0;
}

/* custom classes */
.container {
  margin: 0 auto;
  padding: 0 15px;
  max-width: 45rem;
}

/* display_prop */
.d-block {
  display: block;
}

.d-flex {
  display: flex;
}

/* position_prop */
.p-absolute {
  position: absolute;
}

.p-relative {
  position: relative;
}

/* width_prop */
.w-100 {
  width: 100%;
}

.w-100vw {
  width: 100vw;
}

/* height_prop */
.h-100 {
  height: 100%;
}

.h-100vh {
  height: 100vh;
}

/* cursor_prop */
.c-pointer {
  cursor: pointer;
}

/* border-radius_prop */
.br-50 {
  border-radius: 50%;
}

/* box-shadow_prop */
.z-depth-0 {
  -webkit-box-shadow: none !important;
  box-shadow: none !important;
}

.z-depth-1 {
  -webkit-box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14),
    0 3px 1px -2px rgba(0, 0, 0, 0.12), 0 1px 5px 0 rgba(0, 0, 0, 0.2);
  box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14),
    0 3px 1px -2px rgba(0, 0, 0, 0.12), 0 1px 5px 0 rgba(0, 0, 0, 0.2);
}

.z-depth-2 {
  -webkit-box-shadow: 0 4px 5px 0 rgba(0, 0, 0, 0.14),
    0 1px 10px 0 rgba(0, 0, 0, 0.12), 0 2px 4px -1px rgba(0, 0, 0, 0.3);
  box-shadow: 0 4px 5px 0 rgba(0, 0, 0, 0.14), 0 1px 10px 0 rgba(0, 0, 0, 0.12),
    0 2px 4px -1px rgba(0, 0, 0, 0.3);
}

.z-depth-3 {
  -webkit-box-shadow: 0 8px 17px 2px rgba(0, 0, 0, 0.14),
    0 3px 14px 2px rgba(0, 0, 0, 0.12), 0 5px 5px -3px rgba(0, 0, 0, 0.2);
  box-shadow: 0 8px 17px 2px rgba(0, 0, 0, 0.14),
    0 3px 14px 2px rgba(0, 0, 0, 0.12), 0 5px 5px -3px rgba(0, 0, 0, 0.2);
}

.z-depth-4 {
  -webkit-box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0.14),
    0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -7px rgba(0, 0, 0, 0.2);
  box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0.14),
    0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -7px rgba(0, 0, 0, 0.2);
}

.z-depth-5 {
  -webkit-box-shadow: 0 24px 38px 3px rgba(0, 0, 0, 0.14),
    0 9px 46px 8px rgba(0, 0, 0, 0.12), 0 11px 15px -7px rgba(0, 0, 0, 0.2);
  box-shadow: 0 24px 38px 3px rgba(0, 0, 0, 0.14),
    0 9px 46px 8px rgba(0, 0, 0, 0.12), 0 11px 15px -7px rgba(0, 0, 0, 0.2);
}
